import './App.css'
import React from 'react';

export default class App extends React.Component{
  state = {
    rows:6,
    columns:7,
    moves:[
      {x:0, y:0, player:'red'}
    ],
  };

  getPiece = (x,y)=>{
    const list = this.state.moves.filter((item)=>{
      return (item.x===x && item.y===y);
    });
    return list[0];
  }


  renderBoard(){
    const {rows, columns}= this.state;
    const rowsDisplay = [];

    for(let row=0; row<this.state.rows; row+=1){
      const columnDisplay = [];
      for(let column=0; column<this.state.columns; column+=1){
        const piece = this.getPiece(column,row);
        columnDisplay.push(
          <div style={{width:'8vw', height:'8vw', backgroundColor:'yellow', display:'flex', padding: 5}}>
            <div style={{borderRadius:'50%', backgroundColor:'white', flex:1}}>
              {piece? <div style={{backgroundColor:piece.player, flex:1, borderRadius:'50%', border:'1px solid #333'}}/>:undefined}
            </div>
          </div>
        );
      }
      rowsDisplay.push(
      <div style={{display:'flex', flexDirection:'row'}}>{columnDisplay}</div>
      );
    }

    return(
      <div style={{backgroundColor: 'white', display:'flex', flexDirection:'column'}}>
        {rowsDisplay}
      </div>
    );
  }


  render(){
    const {style} = this.props;

    return (
      <div style={style ? Object.assign({}, styles.container, style):styles.container}>
        <div>
          {this.renderBoard()}
        </div>
      </div>
    );
  }
}

const styles = {
  container:{
    height:'100%',
    padding:5,
    display:'flex',
    justifyContent:'center',
    alignItems:'center'
  }
}
