import './App.css'
import React from 'react';

export default class App extends React.Component{
  state = {
    rows:6,
    columns:7,
    moves:[],
    playerTurn:'red',
  };

  resetBoard = ()=>{
    this.setState({moves:[], winner:null});

  }

  getPiece = (x,y)=>{
    const list = this.state.moves.filter((item)=>{
      return (item.x===x && item.y===y);
    });
    return list[0];
  }

  getVelocity=(xPosition, yPostion, xV, yV)=>{
    const winningMoves = [{x:xPosition, y:yPostion}];
    const player = this.getPiece(xPosition,yPostion).player;

    for(let delta=1; delta<=3; delta+=1){
      const checkX = xPosition + xV * delta;
      const checkY = yPostion + yV * delta;

      const checkPiece = this.getPiece(checkX, checkY);
      if(checkPiece && checkPiece.player === player){
        winningMoves.push({x:checkX, y:checkY});
      }else{
        break;
      }
    }

    for(let delta=-1; delta>= -3; delta-=1){
      const checkX = xPosition + xV*delta;
      const checkY = yPostion + yV*delta;

      const checkPiece = this.getPiece(checkX, checkY);
      if(checkPiece && checkPiece.player === player){
        winningMoves.push({x:checkX, y:checkY});
      }else{
        break;
      }
    }

    return winningMoves;
  }


  checkWinner = (x, y)=>{
    const velocities = [{x:1, y:0}, {x:0, y:1}, {x:-1, y:1}, {x:1, y:1}];
    for(let dex=0; dex<velocities.length; dex++){
      const element = velocities[dex];
      const winningMoves = this.getVelocity(x,y,element.x, element,y);
      if(winningMoves.length>3){
        this.setState({winner:this.getPiece(x,y).player, winningMoves});
      }
    }
    // //check horizontally
    // let winningMoves=[{x,y}];
    // for(let column = x+1; column<x+4; column+=1){
    //   const checkPiece = this.getPiece(column, y);
    //   if(checkPiece && checkPiece.player === player){
    //     winningMoves.push({x:column, y:y});
    //   }else{
    //     break;
    //   }
    // }
    // for(let column = x-1; column>x-4; column-=1){
    //   const checkPiece = this.getPiece(column, y);
    //   if(checkPiece && checkPiece.player ===player){
    //     winningMoves.push({x:column, y:y});
    //   }else{
    //     break;
    //   }
    // }

    // if(winningMoves.length>3){
    //   console.log('winner',winningMoves)
    //   this.setState({winner:player, winningMoves});
    //   return true;
    // }

    // //check vertically
    // winningMoves=[{x,y}];
    // for(let row = y+1; row<y+4; row+=1){
    //   const checkPiece = this.getPiece(x, row);
    //   if(checkPiece && checkPiece.player ===player){
    //     winningMoves.push({x:x, y:row});
    //   }else{
    //     break;
    //   }
    // }
    // for(let row = y-1; row>y-4; row-=1){
    //   const checkPiece = this.getPiece(x,row);
    //   if(checkPiece && checkPiece.player ===player){
    //     winningMoves.push({x:x, y:row});
    //   }else{
    //     break;
    //   }
    // }

    // if(winningMoves.length>3){
    //   console.log('winner',winningMoves)
    //   this.setState({winner:player, winningMoves});
    //   return true;
    // }
  }

  addMove = (x,y)=>{
    const{playerTurn}= this.state;
    const nextPlayerTurn = playerTurn === 'red' ? 'blue' : 'red';
    //check for winner, based on the next move
    this.setState({moves:this.state.moves.concat({x, y, player:playerTurn}), playerTurn:nextPlayerTurn},()=> this.checkWinner(x,y, playerTurn));
  }


  renderBoard(){
    const {rows, columns, winner}= this.state;
    const rowsDisplay = [];

    for(let row=0; row<this.state.rows; row+=1){
      const columnDisplay = [];
      for(let column=0; column<this.state.columns; column+=1){
        const piece = this.getPiece(column,row);
        columnDisplay.push(
          <div onClick={()=>{this.addMove(column, row)}} style={{width:'8vw', height:'8vw', backgroundColor:'yellow', display:'flex', padding: 5, cursor:'pointer'}}>
            <div style={{borderRadius:'50%', backgroundColor:'white', flex:1, display:'flex'}}>
              {piece? <div style={{backgroundColor: piece.player, flex:1, borderRadius:'50%', border:'1px solid #333'}}/>:undefined}
            </div>
          </div>
        );
      }
      rowsDisplay.push(
      <div style={{display:'flex', flexDirection:'row'}}>{columnDisplay}</div>
      );
    }

    return(
      <div style={{backgroundColor: 'white', display:'flex', flexDirection:'column'}}>
        {winner && <div onClick={this.resetBoard} style={{position:'absolute',left:0,right:0,bottom:0,top:0, zIndex:3, backgroundColor:'rgba(0,0,0, .5)',
      display:'flex', justifyContent:'center', alignContent:'center', color:'#fff', fontWeight:'200', fontSize:'8vw' }}>{`${winner} WINS!!`}</div>}
        {rowsDisplay}
      </div>
    );
  }


  render(){
    const {style} = this.props;

    return (
      <div style={style ? Object.assign({}, styles.container, style):styles.container}>
        <div>
          {this.renderBoard()}
        </div>
      </div>
    );
  }
}

const styles = {
  container:{
    height:'100%',
    padding:5,
    display:'flex',
    justifyContent:'center',
    alignItems:'center'
  }
}
