import React from 'react';

export default class App extends React.Component{
  state = {
    rows:6,
    columns:7,
  };

  renderBoard(){
    const {rows, columns}= this.state;
    const rowsDisplay = [];
    for(let row=0; row<this.state.rows; row+=1){
      const columnDisplay = [];
      for(let column=0; column<this.state.columns; column+=1){
        columnDisplay.push(
          <div style={{width:100, height:100, backgroundColor:'while', border:'1px solid #333'}}></div>
        );
      }
      rowsDisplay.push(
      <div style={{display:'flex', flexDirection:'row'}}>{columnDisplay}</div>
      );
    }
    return(
      <div style={{backgroundColor: 'yellow', width: 400, height: 400, display:'flex', flexDirection:'column'}}>
        {rowsDisplay}
      </div>
    );
  }


  render(){
    const {style} = this.props;

    return (
      <div>
        {this.renderBoard()}
      </div>
    );
  }
}
